import com.eviware.soapui.support.XmlHolder
def myTestCase = context.testCase
def counter,next,previous,size
File tickerEnumFile = new File("C:/Users/michaela.datkova/Desktop/Input.txt") //make sure input.txt file already exists and contains different set of values sepearted by new line (CR).
List lines = tickerEnumFile.readLines()
size = lines.size.toInteger()
propTestStep = myTestCase.getTestStepByName("Property - Looper") // get the Property TestStep
propTestStep.setPropertyValue("Total", size.toString())
counter = propTestStep.getPropertyValue("Count").toString()
counter= counter.toInteger()
next = (counter > size-2? 0: counter+1)
tempValue = lines[counter]
propTestStep.setPropertyValue("Value", tempValue)
propTestStep.setPropertyValue("Count", next.toString())
next++
log.info "Reading line : ${(counter+1)} / $lines.size"
propTestStep.setPropertyValue("Next", next.toString())
log.info "Value '$tempValue' -- updated in $propTestStep.name"
if (counter == size-1)
{
propTestStep.setPropertyValue("StopLoop", "T")
log.info "Setting the stoploop property now..."
}
else if (counter==0)
{
def runner = new com.eviware.soapui.impl.wsdl.testcase.WsdlTestCaseRunner(testRunner.testCase, null)
propTestStep.setPropertyValue("StopLoop", "F")
}
else
{
propTestStep.setPropertyValue("StopLoop", "F")
}

// Input and output file paths
def inputFilePath = "C:/Users/michaela.datkova/Desktop/inputFile.txt"
def outputFilePath = "C:/Users/michaela.datkova/Desktop/outputFile.txt"

// Read lines from the input file
File inputFile = new File(inputFilePath)
List inputLines = inputFile.readLines()

// Initialize or clear output file for new data
File outputFile = new File(outputFilePath)
outputFile.text = '' // Clears the output file at the start

// Loop through each line in the input file
inputLines.each { line ->
    // Set the input value as a property in the request
    def propTestStep = context.testCase.getTestStepByName("looper-props") 
    propTestStep.setPropertyValue("Value", line)

    // Run the request test step
    def response = testRunner.runTestStepByName("addRequest") 

    // Capture and write the response to output file
    if (response.getStatus() == com.eviware.soapui.model.testsuite.TestStepResult.TestStepStatus.OK) {
        def responseContent = testRunner.runTestStepByName("addRequest").getResponse().getContentAsString()
        outputFile << "Input: ${line}\nResponse:\n${responseContent}\n\n"
    } else {
        outputFile << "Input: ${line}\nError: Request failed for this input.\n\n"
    }
 }

log.info "Responses saved to: ${outputFilePath}"

